import{_ as t,h as e,i as d,R as a}from"./chunks/framework.7e07dabb.js";const b=JSON.parse('{"title":"键盘使用","description":"","frontmatter":{},"headers":[],"relativePath":"src/develop/vim/keybord.md","filePath":"src/develop/vim/keybord.md","lastUpdated":1692932276000}'),r={name:"src/develop/vim/keybord.md"},h=a('<h1 id="键盘使用" tabindex="-1">键盘使用 <a class="header-anchor" href="#键盘使用" aria-label="Permalink to &quot;键盘使用&quot;">​</a></h1><h2 id="常用功能" tabindex="-1">常用功能 <a class="header-anchor" href="#常用功能" aria-label="Permalink to &quot;常用功能&quot;">​</a></h2><table><thead><tr><th>功能</th><th>组合键</th><th>备注</th></tr></thead><tbody><tr><td>唤醒</td><td>f + j</td><td>f 和 j 键⼀起按</td></tr><tr><td>休眠</td><td>Lshift + Rshift + p</td><td>⼿动休眠键盘</td></tr><tr><td>切换蓝⽛设备</td><td>Lshift + Rshift + q / w / e</td><td>q / w / e 表⽰三个不同的蓝⽛频道</td></tr><tr><td>重新⼴播当前蓝⽛频道</td><td>Lshift + Rshift + r</td><td>⽆法连接当前蓝⽛频道的时候可以尝试</td></tr><tr><td>清空当前蓝⽛频道绑定的设备</td><td>Lshift + Rshift + o</td><td>e.g. 如果你当前的蓝⽛频道是 q 频道，按此组合键后，q 频道绑定的设备被清除，可⽤于绑定其他蓝⽛设备</td></tr><tr><td>输出电池电量</td><td>Lshift + Rshift + h</td><td>在任意编辑器上按组合键，可以打印出数字，表⽰当前剩余电量</td></tr></tbody></table><h2 id="连接蓝牙" tabindex="-1">连接蓝牙 <a class="header-anchor" href="#连接蓝牙" aria-label="Permalink to &quot;连接蓝牙&quot;">​</a></h2><p>HTKB 可以⽀持绑定三台不同的蓝⽛设备。其快捷键是同时按住 Lshift + Rshift + 蓝⽛频 道按键。固件中默认的蓝⽛频道按键为 q，w 和 e。使⽤组合键后可以在电脑的在添加蓝 ⽛设备清单中找到HTKB开头的设备 （注意：不要⼀次把 Lshift + Rshift + q，Lshift + Rshift + w，Lshift + Rshift + e 都按⼀ 遍，因为会傻傻分不清楚。解决⽅法是等⼀会⼉蓝⽛设备清单更新后再次按 Lshift + Rshift + r 重新⼴播当前蓝⽛频道）</p><h2 id="解除蓝牙" tabindex="-1">解除蓝牙 <a class="header-anchor" href="#解除蓝牙" aria-label="Permalink to &quot;解除蓝牙&quot;">​</a></h2><p>解除蓝牙设备的思路也是相似：如果我想解除 fn + w 的设备，那么我首先需要切换到fn + w 频道，然后按 fn + esc 解除该频道绑定的设备</p>',7),s=[h];function i(o,f,n,l,c,_){return e(),d("div",null,s)}const m=t(r,[["render",i]]);export{b as __pageData,m as default};
